INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2065, 'Frontend Entwickler (m/w) at Key-Work Consulting GmbH (Karlsruhe, Deutschland)', '&lt;p&gt;Ihre Aufgaben&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Entwicklung von Anwendungen im J2EE-Umfeld mit HTML5, CSS und JavaScript / jQuery (Frontend-Programmierung, Anbindung bestehender Back&amp;shy;end-Prozesse)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Gestaltung von Benutzeroberfl&amp;auml;chen mit beson&amp;shy;derem Augenmerk auf Usability und UX&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Mitwirkung bei der Anforderungserhebung im Projektteam&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Entwicklung der L&amp;ouml;sungsidee von visueller Seite (Scribbles / Mockups)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Qualit&amp;auml;tssicherung, Wartung, technische Doku&amp;shy;mentation und Second-Level-Support f&amp;uuml;r die entwickelten Anwendungen&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Karlsruhe, Deutschland', 'http://careers.stackoverflow.com/jobs/96976/frontend-entwickler-m-w-key-work-consulting-gmbh', 'Sat, 03 Oct 2015 14:02:02 Z', '2015-10-03T14:02:02Z', 2065);
