INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2067, 'Junior Developer - C#/.NET at CineMassive (Atlanta, GA)', '&lt;p&gt;&lt;span&gt;&lt;span&gt;&lt;span&gt;This is a great opportunity for an entry-level developer seeking to be mentored by senior developers in a commercial software environment. We look for smart, creative, problems solvers interested in product application of computer vision and high-performance computing. Ideal candidates will appreciate that green field development requires rigor and discipline to ensure quality innovation.&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;&lt;strong&gt;&lt;span&gt;&lt;span&gt;Technology Environment&lt;/span&gt;&lt;br&gt;&lt;/span&gt;&lt;/strong&gt;TDD, C#, .NET 4.5, MVVM Design Pattern, WCF, WPF, GitHub, high performance computing, 3D interface design&lt;/span&gt;&lt;/p&gt;', 'Atlanta, GA', 'http://careers.stackoverflow.com/jobs/87158/junior-developer-c-sharp-net-cinemassive', 'Sat, 03 Oct 2015 14:02:02 Z', '2015-10-03T14:02:02Z', 2067);
