INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2079, 'Front End Software Engineer at McLaren Applied Technologies (Woking, UK)', '&lt;p&gt;Front end software engineers at McLaren Applied Technologies create innovative solutions using the latest open source technologies. We need smart engineers who are passionate about technology to join our team and help us provide timely insight into data for our customers. Your technical expertise will help us design, develop, test, deploy, and enhance software solutions that display and analyze data quickly and efficiently. You could be writing a web application with &lt;a href="https://angularjs.org/" rel="nofollow" rel="nofollow"&gt;AngularJS&lt;/a&gt; or a desktop application with &lt;a href="http://electron.atom.io/" rel="nofollow" rel="nofollow"&gt;Electron&lt;/a&gt;.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Responsibilities:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Create efficient and reusable front-end solutions.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop small prototypes and spikes that reduce risk.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Actively contribute to agile ceremonies.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Woking, UK', 'http://careers.stackoverflow.com/jobs/66687/front-end-software-engineer-mclaren-applied-technologies', 'Sat, 03 Oct 2015 12:02:02 Z', '2015-10-03T12:02:02Z', 1910);
