INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2084, 'Softwareentwickler / Cloud Services (m/w) at ITM Beratungsgesellschaft mbH (Stuttgart, Deutschland)', '&lt;p&gt;&amp;nbsp;&lt;strong&gt;DAS SIND DEINE AUFGABEN&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Du konzipierst und entwickelst komplexe Anwendungen in unseren kompetenten Projektteams&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Du analysierst und strukturierst die Anforderungen unserer Kunden vor Ort&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Du ber&amp;auml;tst unsere Kunden professionell bei der Einf&amp;uuml;hrung und Weiterentwicklung von Technologien&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Du arbeitest dich selbstst&amp;auml;ndig in innovative Technologien ein und schaffst daraus einen Mehrwert f&amp;uuml;r unsere Kunden&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Stuttgart, Deutschland', 'http://careers.stackoverflow.com/jobs/94235/softwareentwickler-cloud-services-m-w-itm-beratungsgesellschaft-mbh', 'Sat, 03 Oct 2015 11:02:02 Z', '2015-10-03T11:02:02Z', 2084);
