INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2089, 'Software Development Engineer C/C++ at BSH HOME APPLIANCES GROUP (Giengen, Germany)', '&lt;p&gt;&lt;strong&gt;Strengthen the development team in our international competence center for refrigerators and freezers in Giengen near Ulm as Software Development Engineer&lt;br&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;YOUR TASKS&lt;/strong&gt; program micro controllers in C/C++ &amp;bull; contribute to the creation of software architectures and designs &amp;bull; conduct code reviews, module tests and integration tests &amp;bull; create specifications and documentations &amp;bull; work in virtual teams&lt;/p&gt;', 'Giengen, Germany', 'http://careers.stackoverflow.com/jobs/94542/software-development-engineer-c-c-plus-plus-bsh-home-appliances-group', 'Sat, 03 Oct 2015 10:02:02 Z', '2015-10-03T10:02:02Z', 947);
