INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2095, 'Full stack/PHP engineer at Edukey Education Ltd (Edinburgh, UK)', '&lt;p&gt;We are&amp;nbsp;a successful EdTech company,&amp;nbsp;making great software for school teachers &amp;amp;&amp;nbsp;finding innovative ways to solve real problems that teachers are facing in the classroom. To continue doing that at ever increasing pace, we are looking for a full stack software engineer to join us.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Location&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are more than happy for&amp;nbsp;you to work from home, however availability for frequent physical meetings&amp;nbsp;in Edinburgh area is essential.&amp;nbsp;&lt;/p&gt;', 'Edinburgh, UK', 'http://careers.stackoverflow.com/jobs/94741/full-stack-php-engineer-edukey-education-ltd', 'Sat, 03 Oct 2015 09:02:02 Z', '2015-10-03T09:02:02Z', 2095);
