INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2105, 'Senior Platform Engineer at Harry''s (New York, NY)', '&lt;strong&gt;The Role&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;As a Senior Platform Engineer at Harry&amp;rsquo;s, you&amp;rsquo;ll own, architect and expand key pieces of our app ranging from pick and pack systems to distribution optimization to global expansion.You&amp;rsquo;ll work across the stack with a focus on the back end, work in small product teams, and ship code every day.&amp;nbsp;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;&lt;strong&gt;The Team&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;We chose to build an entirely custom e-commerce platform to power the business, with a focus on exceptional user experience, performance, reliability, and developer productivity. We strive to be lean,&amp;nbsp;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span&gt;The Engineering Team at Harry''s drives online experiences for customers and guides internal decision making. The team embraces and encourages a culture and build process that allows the things we build to be open-sourc&lt;/span&gt;&lt;span&gt;ed&lt;/span&gt;&lt;span&gt;, meaning our code and engineering products are of the highest quality - kind of like our razors.&amp;nbsp; You can learn more about the team and culture on &lt;/span&gt;&lt;span&gt;&lt;a href="http://engineering.harrys.com/2013/10/08/open-source-and-engineering-culture.html" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;the engineering blog&lt;/a&gt;&lt;/span&gt;&lt;span&gt;.&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;Responsibilities:&amp;nbsp;&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Own, architect and build core systems such as supply chain, as well as user facing features for our API layer.&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Determine and strategize ways to operate more efficiently, like optimizing our inventory and shipping algorithms&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Think about performance all the time, and proactively work to make our platform better&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Communicate and work effectively with engineers, Product, and the business at-large.&amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Mentor junior engineers, through direct pairing and mentoring, or setting a good example.&amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/94520/senior-platform-engineer-harrys', 'Sat, 03 Oct 2015 01:02:02 Z', '2015-10-03T01:02:02Z', 2105);
