INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2116, 'Software Engineer (C++) at Daylight Solutions (San Diego, CA)', '&lt;p&gt;Would you like to be a important part of an exceptional engineering team, designing and devloping next generation laser products?!&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This person will be&amp;nbsp;responsible for participating in the design, development, validation and documentation of application software to support the test and manufacturing of laser based products.&amp;nbsp; This role also allows you to collaborate&amp;nbsp;and task in support of other departments, disciplines and external customers on new products, existing products in production, and legacy fielded products.&amp;nbsp; Primary development tasks include management of software requirements and specifications, design and documentation of software architecture, source code development and management, end production validation and testing.&amp;nbsp; This position requires a solid foundation in Windows based PC software application development&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;PRIMARY DUTIES AND RESPONSIBILITIES:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Develop and maintain software supporting the production of laser systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Integrate new instruments and test equipment into the production and test process&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design and implement new requirements into software and database design&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Automate report generation&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide maintenance support for existing applications&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'San Diego, CA', 'http://careers.stackoverflow.com/jobs/96938/software-engineer-c-plus-plus-daylight-solutions', 'Sat, 03 Oct 2015 00:02:02 Z', '2015-10-03T00:02:02Z', 2116);
