INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2123, 'Head of Engineering (Ruby/Rails) at Guide Financial (San Francisco, CA)', '&lt;p&gt;If you are Ruby/Rails rockstar, committed to changing the world, are excited by audacious technical challenges, have experience leading&amp;nbsp;a high performing team, and also love rolling up your sleeves and directly contributing to software development (about 50% of the time) &amp;ndash; this is your dream job.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is a critical position for us (we consider it our "third co-founder"), and will impact all aspects of our company. Ruby on Rails expertise is a requirement, as is prior experience managing a team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;a title="https://www.guidefinancial.com" href="https://www.guidefinancial.com/"&gt;Read more about Guide Financial here.&lt;/a&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Hire and manage an engineering team to build world-class software products that are well-designed, thoroughly tested, and highly scalable&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Serve as a inspiring technology leader to develop, mentor, retain and grow a high performing team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide significant and ongoing individual software contributions as part of the development roadmap&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop and collaborate on key architectural decisions and design considerations, within Guide Financial and across John Hancock Labs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Drive successful project execution on schedule and within budget&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ensure software engineering best practices are utilized and continuously monitored and improved&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work closely with other stakeholders (including design, data science, business strategy and operations, and others) to develop and deliver a product requirements&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Continuously monitor and upgrade software to maintain industry-leading security and compliance requirements&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/96933/head-of-engineering-ruby-rails-guide-financial', 'Fri, 02 Oct 2015 23:02:02 Z', '2015-10-02T23:02:02Z', 2123);
