INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2125, 'Network/Systems Engineer at Fintech (Tampa, FL)', '&lt;p&gt;The Network/Systems Engineer is responsible for the day-to-day operations of the company&amp;rsquo;s technical systems and infrastructure. The Network/Systems Engineer ensures the core company systems are available in a 24x7 environment, provides research and guidance in maintaining scalable systems with 99% uptime, and ensures all systems are secured.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;This position is in the IT department and reports to the Director, IT and Operations.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Duties &amp;amp; Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Provides hardware support and networking services to internal departments (computers, phones, printers, network access, fax/copy machines) and external clients&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Perform network management to include controlling, planning, allocating, deploying, coordinating, and monitoring the resources necessary for proper network operations, maintenance and services&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Conduct security-centric infrastructure components and various scanning tools&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide expertise in the current network topologies, protocols, equipment and utilizes state-of-the art network management tools for monitoring, collecting, analyzing and reporting system performance data&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Configure and maintain Cisco routers, Nexus switches, ASA Firewalls, wireless, and all associated network equipment, including creating /maintaining routing tables, Access Control Lists (ACLs) and Virtual Local Area Networks (VLANs)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with outside consultants as appropriate for independent security audits, penetration tests, SSAE16 and customer related audits&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Administer new and existing Windows and Linux servers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide VMware/Virtualization Support and Maintenance&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Proactively identify potential issues in the production environment and help recommend solutions&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in the team&amp;rsquo;s on-call support rotation&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Patch Management&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Backup Management&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Tampa, FL', 'http://careers.stackoverflow.com/jobs/99452/network-systems-engineer-fintech', 'Fri, 02 Oct 2015 23:02:02 Z', '2015-10-02T23:02:02Z', 2125);
