INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2142, 'Software Engineer @ High Growth Startup (Java, Web, SaaS, Big Data) at ArchiveSocial (Durham, NC)', '&lt;p&gt;ArchiveSocial is a fast growing cloud-based SaaS company located at the American Underground in downtown Durham, NC. Our dev team consists of experienced, top-ranked ex-IBMers and we&amp;rsquo;re eager to grow our core team with a talented developer who fits the following description:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Likes Java, isn&amp;rsquo;t afraid to admit it, and is darn good at it&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Lives and breathes the web, SaaS software, startups, and internet/industry trends&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Believes this is an object-oriented world, yet knows when it&amp;rsquo;s the right time to get down and functional&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Has an ability to design components and frameworks that is just as impressive as the ability to implement them&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Highly skilled in web technologies, particularly Javascript and CSS&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Energized by working across the stack (front-end, back-end, &amp;hellip;)&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Fundamentally understands concepts such as REST, MVC, and software Design Patterns&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Believes that clean code is better code: must be very detailed oriented and disciplined in terms of code quality and conventions&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Experience with Eclipse, AWS, Play Framework, and Dojo are all a huge plus&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;This position is open to both entry-level and more experienced applicants. Professional experience and a B.S in Computer Science (or equivalent) are required.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;PLEASE NOTE: ArchiveSocial is unable to sponsor visas. You must be a U.S. Citizen or Permanent Resident authorized to work in the U.S. Also, this is not a remote position. Re-location assistance is not currently available.&lt;/p&gt;', 'Durham, NC', 'http://careers.stackoverflow.com/jobs/89375/software-engineer-high-growth-startup-java-archivesocial', 'Fri, 02 Oct 2015 22:02:02 Z', '2015-10-02T22:02:02Z', 2142);
