INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2149, 'DevOps Engineer at Growing EdTech Company at CaseNEX (Charlottesville, VA)', '&lt;p&gt;If you''ve always wanted to use your skills to make education better through the use of technology then this is the job for you.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We build web-based products that are intuitive and designed by educators for educators and we need your help to get them off the ground and keep them running smoothly.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You''ll work with AWS and develop programmatic solutions to our growing infrastructure.&lt;/p&gt;', 'Charlottesville, VA', 'http://careers.stackoverflow.com/jobs/99450/devops-engineer-at-growing-edtech-company-casenex', 'Fri, 02 Oct 2015 22:02:02 Z', '2015-10-02T22:02:02Z', 2149);
