INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2151, 'Software Engineer at JW Player (New York, NY)', '&lt;p&gt;&lt;strong&gt;A Software Engineer in Automation delights in never doing something by hand more than once. &lt;/strong&gt;JW Player is looking for a talented Software Engineer experienced in building test automation frameworks, and automation of the build and deploy process. &amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This role will embed on the &lt;strong&gt;Data&lt;/strong&gt; &lt;strong&gt;Team&lt;/strong&gt; and will be focused on automation of &lt;strong&gt;Big Data&lt;/strong&gt; products.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;In this role, you will:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Collaborate with development teams to understand how development changes impact ongoing automation efforts&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Automate the build and deployment process for the Data Team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build full-stack automated test frameworks&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop automated tests to validate Big Data solutions (1-2 Terabytes per day)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Take an active role in increasing the percentage of automated versus manual test cases&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work as part of a scrum team in an agile environment&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/99448/software-engineer-jw-player', 'Fri, 02 Oct 2015 22:02:02 Z', '2015-10-02T22:02:02Z', 2151);
