INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2153, 'Remote Linux SysAdmin at Kira Inc. (Toronto, ON, Canada) (allows remote)', '&lt;p&gt;Kira Systems is looking for a Linux Sysadmin that is available on an ongoing basis from 0600-1400 UTC, Monday&amp;mdash;Friday. Duties include server monitoring, maintenance, and support for our clients working outside of North American business hours.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Although we are based in the Toronto area we are aiming to hire someone in Europe/South Africa or Australia/New Zealand/Japan &amp;mdash; this role is intended for a remote candidate.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The role is aimed at the Junior to Intermediate level: our ideal candidate would have good familiarity with technologies mentioned below, the drive to learn more about them, and some demonstrated ability to dig into ambiguous problems and find a solution.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This position starts as soon as possible.&lt;/p&gt;', 'Toronto, ON, Canada', 'http://careers.stackoverflow.com/jobs/99447/remote-linux-sysadmin-kira-inc', 'Fri, 02 Oct 2015 22:02:02 Z', '2015-10-02T22:02:02Z', 2153);
