INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2161, 'Systems Engineer - $30K at Crossover (Bucharest, Romania) (allows remote)', '&lt;p&gt;Systems Engineer - $30K&lt;/p&gt; &lt;br&gt; &lt;p&gt;Do you enjoy working on complex technical problems? Do you like a dynamic environment where you have the opportunity to give feedback and constantly improve upon current processes? If you are experienced in working with a wide variety of technologies, enjoy communicating technical solutions to customers, like being a part of a team and are a proactive problem solver - then this role is for you.&lt;/p&gt; &lt;br&gt; &lt;p&gt;Aurea is an international enterprise software company that helps large global enterprises like Disney, British Airways, and eBay create transformative experiences for their end customers. Aureas suite of technologies and managed service offerings enable companies in select vertical markets (travel &amp;amp; leisure, retail, energy, and insurance) to design, execute, monitor, and optimize end-to-end customer journeys.&lt;/p&gt; &lt;br&gt; &lt;p&gt;Job Description&lt;/p&gt; &lt;p&gt;A Systems Engineer in Aurea IT works with a wide range of modern technologies. The successful candidate will be a part of the global IT team who is supporting global Aurea operations. Candidate should have the desire and flexibility to work on a wide range of challenging IT related projects.&lt;/p&gt; &lt;br&gt;', 'Bucharest, Romania', 'http://careers.stackoverflow.com/jobs/97750/systems-engineer-30k-crossover', 'Fri, 02 Oct 2015 21:02:02 Z', '2015-10-02T21:02:02Z', 2161);
