INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2165, 'Test Automation Engineer ATC Global - US$30k at Crossover (Bengaluru, India) (allows remote)', '&lt;p&gt;There are those who just &amp;ldquo;record&amp;rdquo; tests, and then there are those who write tests. Are you the real thing? Then we want you for our US Test Automation Engineer position.&lt;/p&gt; &lt;p&gt;Work with top global talent on big time enterprise apps for Fortune 500 companies in a career-building environment. It''s 100% remote, work from home, with minimal meetings, where you can choose when you put in your 40 hours most effectively each week.&lt;/p&gt;  &lt;p&gt;We need you to engineer inputs/outputs: Input well-documented and standard format test scenarios; Output automated tests&lt;/p&gt;  &lt;p&gt;The successful candidate will use the TestComplete IDE and several other automated tools either on your desktop or the virtual environment of a remote desktop. Occasionally you may use some JavaScript, but most of your writing will be in the basic TestComplete graphical language. TestComplete will be easy for you if you&amp;rsquo;ve developed web or desktop applications before. You will write tests across a wide variety of applications and technologies, and your work must measure well against the standard process and quality bar for test engineers.&lt;/p&gt; &lt;p&gt;&lt;br&gt; &lt;strong&gt;Total compensation&lt;/strong&gt; is US$15/hr for a full-time, 40-hour work week.&lt;/p&gt;', 'Bengaluru, India', 'http://careers.stackoverflow.com/jobs/98476/test-automation-engineer-atc-global-us-30k-crossover', 'Fri, 02 Oct 2015 21:02:02 Z', '2015-10-02T21:02:02Z', 2161);
