INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2200, 'Senior Developer - Consulting &amp; Delivery! at ThoughtWorks (New York, NY)', '&lt;em&gt;&lt;strong&gt;As a Senior&amp;nbsp;Developer at ThoughtWorks, here''s what you can do:&lt;/strong&gt;&lt;/em&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work on large-scale, custom distributed software systems using Java, Ruby and/or .NET&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be responsible for the quality of software and resolving any issues regarding client satisfaction&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be a mentor and role model to less experienced developers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Employ Agile development including task estimation, test automation, deployment automation, and Continuous Integration to improve overall execution speed and product quality&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Travel the world&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Learn something new everyday&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Speak at conferences&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write blogs and books&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Start new offices in places you&amp;rsquo;ve never been before&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Think through hard problems, and work with a team to implement a solution&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work in a dynamic, collaborative, transparent, non-hierarchal, and ego-free culture where your talent is valued over a role title&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop your career outside of the confinements of a traditional career path by focusing on what you&amp;rsquo;re passionate about rather than a predetermined one-size-fits-all plan&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/96918/senior-developer-consulting-delivery-thoughtworks', 'Fri, 02 Oct 2015 21:02:02 Z', '2015-10-02T21:02:02Z', 1586);
