INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2207, 'Network Engineer at TeleTracking Technologies (Pittsburgh, PA)', '&lt;p&gt;Teletracking software solutions create real time results for real-life hospital challenges; powering intelligent care delivery.&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Our network engineers are responsible for the implementation of secure and highly available networking solutions in support of corporate initiatives, including separate physical infrastructures for production, sandbox, and performance testing, and multiple geographic locations. Ensures secure and highly available access to corporate resources, including secure remote access via site-to-site IPSec VPN, client IPSec VPN, SSL VPN, and secure web servers.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Performs traffic analysis to determine root cause of network issues.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Implements, administers, maintains, and documents Internet Protocol (IP) addressing, including static IP addresses and Dynamic Host Configuration Protocol (DHCP) scopes.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintains change management records for all network modifications.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Supports local and remote personnel in the installation, configuration, and troubleshooting of LAN and WAN components.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Assists with the administration and maintenance of telephone system, including telephone switches.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Responds to Incidents and Requests directly and servers as escalation point for IT&amp;amp;S Help Desk.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Pittsburgh, PA', 'http://careers.stackoverflow.com/jobs/99441/network-engineer-teletracking-technologies', 'Fri, 02 Oct 2015 21:02:02 Z', '2015-10-02T21:02:02Z', 2207);
