INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2215, 'Android Engineer at Buzz Technologies, Inc. (Mountain View, CA)', '&lt;p&gt;As a&amp;nbsp;Lead Android&amp;nbsp;Engineer, you will be on the team responsible for helping to bring our fanciful mobile messaging products to Android. You will collaborate with the team of engineers, user experience, and visual designers to deliver apps that push the boundaries of what is possible in the mobile messaging space. You will promote excellence in Android/Java/NDK development, creating new features, improving code quality, productivity, and skills through processes, projects, learning, and mentorship. Your passion for building scalable, robust code is supported by your curiosity, understanding, and application of emerging technologies that benefit your work.&lt;/p&gt;', 'Mountain View, CA', 'http://careers.stackoverflow.com/jobs/99438/android-engineer-buzz-technologies-inc', 'Fri, 02 Oct 2015 21:02:02 Z', '2015-10-02T21:02:02Z', 2215);
