INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2218, 'NUVI needs RUBY at NUVI - Social Media Monitoring (Lehi, UT)', '&lt;p&gt;NUVI is seeking a Sr. Ruby Engineer to maintain and improve our Social Media Listening &amp;amp; Analytics platform, allowing us to release stable software at an accelerated pace.&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Implement simple solutions to complex problems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintain existing products and systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Previous experience with MongoDB and Redis&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build features and fix issues as determined by communication with our customers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Have a working knowledge of technologies such as Ruby, and Ruby on Rails, C#, ASP.NET, JavaScript, MongoDB, Redis, PostgreSQL&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Communicate effectively with other Engineers, QA, and Support teams&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Lehi, UT', 'http://careers.stackoverflow.com/jobs/96901/nuvi-needs-ruby-nuvi-social-media-monitoring', 'Fri, 02 Oct 2015 21:02:02 Z', '2015-10-02T21:02:02Z', 2218);
