INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2223, 'UI Engineer at Radius (San Francisco, CA)', '&lt;p&gt;Candidates will be working side-by-side with product designers and engineers to create front-end prototypes, proofs of concepts, and production code for the Radius platform. Expertise writing semantic, modular front-end code using HTML5, CSS3, and W3C coding practices a must. Understanding of the concepts of progressive enhancement, mobile first and future-friendly design are highly encouraged.&lt;/p&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/99435/ui-engineer-radius', 'Fri, 02 Oct 2015 21:02:02 Z', '2015-10-02T21:02:02Z', 2223);
