INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2227, 'Software Architect at Tech Dynamism (Charlottesville, VA) (allows remote)', '&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Position Overview:&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;Our Software Architects help design and build fundamental components and frameworks to be used across applications for our clients. &amp;nbsp;They have a deep understanding of the field&amp;rsquo;s concepts, practices and procedures. &amp;nbsp;This understanding will help to apply a solid foundation for our Software Developers to utilize. &amp;nbsp;The position equates to a mix of approximately 25% software development, 50% software design and analysis and 25% oversight and support for software developers. &amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Essential Functions:&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Understand and apply software design patterns&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Create POCs (Proof of Concepts) for new components and frameworks&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Clearly document and communicate software process flows&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Ability to develop software, staying current with the software engineering standards of the company&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Ability to train and assist software developers to mature and progress their skills&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Charlottesville, VA', 'http://careers.stackoverflow.com/jobs/99431/software-architect-tech-dynamism', 'Fri, 02 Oct 2015 21:02:02 Z', '2015-10-02T21:02:02Z', 2227);
