INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2234, 'Lead/Senior Full Stack Developer at Scarlet (New York, NY)', '&lt;p&gt;&lt;strong&gt;About Scarlet:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Scarlet is a new kind of a voice based information service that delivers personalized content at the right time.&amp;nbsp; We believe that there is a tremendous opportunity to create an effective &lt;em&gt;and&lt;/em&gt; human solution to deal with the ever growing stream of information pouring out of our devices.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;While exploring new opportunities for voice interfaces, we are first creating a consumer mobile application that helps people better start their day.&amp;nbsp; We are a small but fast growing early stage startup that is funded by the Samsung Accelerator.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Position Description:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Scarlet is looking for a founding engineer who can take a lead role in solving complex engineering problems to deliver highly relevant information to users in a natural, helpful and charming way.&amp;nbsp; Ideal candidates are individuals who are strong individual contributors, but also enjoy mentoring and working with other engineers.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will be our founding engineer to build and lead a high performing team that explores new applications of natural language processing, intelligent content aggregation, and voice-driven user interfaces.&amp;nbsp; Deep experience with agile product development, a love for human centered design, expertise in JavaScript development with focus on Node.js, and a passion to solve hard problems with creative solutions are all required to thrive in our fast-paced startup environment.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;You will:&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Build and deploy a scalable backend to support end user experience&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Advance our web based tools and enhance our ability to deliver unique and personalized end user experiences&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Integrate new sources of data into the system&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design and implement machine-based solutions to select and present data for end users&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Analyze system performance and identify areas for improvement&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Play a key role in building a top notch engineering team around you&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work closely with product, design and client teams to define, iterate and implement new features&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/96913/lead-senior-full-stack-developer-scarlet', 'Fri, 02 Oct 2015 20:02:02 Z', '2015-10-02T20:02:02Z', 2234);
