INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2237, 'Senior Front-End Software Engineer at Radius (San Francisco, CA)', '&lt;p&gt;We are looking for experienced front-end/full-stack engineers to complement our current app team. A senior front-end engineer would take ownership of all or part of any of the applications built on top of the Radius Data Platform. This would include, design and architecture of software features, working with designers and product managers to understand and influence the roadmap, and implement both the AngularJS-based UI and RESTful resources in the server for new features.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The application team aims to move fast, and expect you to do this as well. We value autonomy and self-reliance, an eye for robust and maintainable code, and above all the ability to ship.&lt;/p&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/99429/senior-front-end-software-engineer-radius', 'Fri, 02 Oct 2015 20:02:02 Z', '2015-10-02T20:02:02Z', 2223);
