INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2248, 'Senior .Net Developer at Bethlehem Shared Services (Bethlehem, PA)', '&lt;p&gt;&lt;strong&gt;Primary Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Support and improve a custom e-commerce platform written in C# .NET 4.0 MVC 3 and SQL&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Support and improve a series of custom distributed applications which manage order fulfillment and inventory management, written in .NET, ColdFusion, and SQL&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Create and maintain WCF services and other supporting applications&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Write performant SQL stored procedures and propose database designs as required&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Follow best practices to build maintainable, scalable applications and services&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Coordinate with business users to gather requirements&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Manage multiple priorities and projects to achieve business needs on time&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Continually develop skills to stay current with best practices and technologies&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Actively participate in ongoing design and architecture discussions&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Provide mentoring and architecture/development advice to other members of the team&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Bethlehem, PA', 'http://careers.stackoverflow.com/jobs/96910/senior-net-developer-bethlehem-shared-services', 'Fri, 02 Oct 2015