INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2251, 'Senior Software Engineer at Tech Dynamism (Charlottesville, VA) (allows remote)', '&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Position Overview:&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our Senior Software Engineers help design and build fundamental application functionality. &amp;nbsp;They have a deep understanding of the field&amp;rsquo;s concepts, practices and procedures. &amp;nbsp;They lead by example and provide necessary guidance and support for the teams they work with. &amp;nbsp;The position equates to a mix of approximately 75% software development and 25% oversight and support for software developers. &amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Essential Functions:&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Understand and apply software design patterns&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create POCs (Proof of Concepts) for new components and frameworks&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Clearly document and communicate software process flows&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ability to develop software, staying current with the software engineering standards of the company&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ability to train and assist software engineers to mature and progress their skills&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Charlottesville, VA', 'http://careers.stackoverflow.com/jobs/99424/senior-software-engineer-tech-dynamism', 'Fri, 02 Oct 2015 20:02:02 Z', '2015-10-02T20:02:02Z', 2227);
