INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2259, 'Senior Dev/Ops Engineer at ReverbNation (Durham, NC)', '&lt;p&gt;&lt;strong&gt;Senior Dev/Ops Engineer&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;High traffic, feature rich web sites and mobile apps necessitate the usage of many special purpose technologies. &amp;nbsp;A generic LAMP stack just doesn&amp;rsquo;t cut it. &amp;nbsp;However, along with all of the performance and redundancy comes some difficult troubleshooting and interesting operational issues. &amp;nbsp;If you can evaluate complex architectures and devise elegant solutions, we need you! &amp;nbsp;ReverbNation&amp;rsquo;s scale and culture pushes us to leverage new and exciting technologies. &amp;nbsp;The problems are complex, varied, and enjoyable. This position includes both new development and operational problem resolution. Off-hours requirements are light and shared. &amp;nbsp;&lt;/p&gt;', 'Durham, NC', 'http://careers.stackoverflow.com/jobs/96813/senior-dev-ops-engineer-reverbnation', 'Fri, 02 Oct 2015 20:02:02 Z', '2015-10-02T20:02:02Z', 2259);
