INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2261, 'Senior Web Developer at ReverbNation (Durham, NC)', '&lt;p&gt;&lt;strong&gt;Senior Web Developer &amp;ndash; Durham, NC&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Come work for a music company that has been voted one of the &amp;lsquo;Best Places to Work&amp;rsquo; in the Triangle for 3 years running.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Successful candidates are individuals who thrive on collaborative teams where everyone works on everything, and everyone is encouraged to contribute ideas across all levels of the organization.&amp;nbsp; You should desire to work in a fast moving and adaptive start-up environment but with the stability, compensation and benefits of a successful company.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a&amp;nbsp;&lt;strong&gt;Senior Web Developer&lt;/strong&gt; you will use your web development skills to develop applications and systems that will help musicians be more successful and accelerate the growth of ReverbNation.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What you will do:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design and implement high quality, elegant SaaS software for desktop, mobile web and mobile apps&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate directly with product management, marketing, design and engineering teams on new product features&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Help define the processes and frameworks to support a high-performing engineering organization&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Lead and mentor other team members while being a subject-matter expert in your field&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Durham, NC', 'http://careers.stackoverflow.com/jobs/96815/senior-web-developer-reverbnation', 'Fri, 02 Oct 2015 20:02:02 Z', '2015-10-02T20:02:02Z', 2259);
