INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2265, 'DevOps Engineer: $35M funded, Relo offered at Distil Networks (San Francisco, CA)', '&lt;p&gt;If you are by nature a developer who has grown to appreciate the interdependence of software development and IT operations and you are excited to help a startup, like Distil, produce software and IT services more rapidly, we want to hear from you.&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&amp;nbsp;Automate management of Distil''s CDN infrastructure at scale. This encompasses hundreds of machines, data centers spread across multiple continents, and environments including both co-lo, managed, and cloud infrastructure.&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Built out a monitoring system to ensure 99.99% uptime across environments.&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build out release systems that enable engineering teams to develop without having to worry about integrations or dependancies&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Opportunity to scout out battle tested, fast and scalable technologies to allow our global platforms to maintain their blistering growth&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/99422/devops-engineer-35m-funded-relo-offered-distil-networks', 'Fri, 02 Oct 2015 20:02:02 Z', '2015-10-02T20:02:02Z', 2265);
