INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2269, 'iOS Developer with strong Product focus at ParkWhiz (Chicago, IL)', '&lt;p&gt;ParkWhiz is looking for an experienced iOS software engineer with a product management bent to lead iOS development.&amp;nbsp;You will have a tremendous amount of influence over the direction of our mobile products and user experience.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Here are some examples of the types of things you will do at ParkWhiz:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Take our lofty goals for the perfect parking app of the future and break them up into intermediate, actionable projects&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be the resident iOS expert and champion. Make us aware of the ecosystem, trends, upcoming paradigm shifts, new tech, etc. We have a great history of adhering to best practies on the platform, and being featured by Apple in a variety of contexts, so help us maintain and improve this&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experiment with new hardware: in-dash systems, BLE beacons, parking access control equipment, etc, and keep us at the cutting edge&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Assess the complexity, feasibility, and impact of integrations with potential partners and vendors.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Prototype new ideas and validate them with users&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Continually assess analytics results (and add new analytics when needed) to identify UX issues, A/B test UI changes, and generally iterate our way to a better user experience&lt;/li&gt;&lt;br /&gt;&lt;li&gt;If you have the inclination and desire, you may jump in and build out some of the new APIs you need onto our PHP, Ruby, or Go backends&lt;/li&gt;&lt;br /&gt;&lt;li&gt;And of course, write loads of iOS software&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;If you have any questions please feel free to email mark@parkwhiz.com to learn if this position is the right fit for you.&lt;/p&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/99266/ios-developer-with-strong-product-focus-parkwhiz', 'Fri, 02 Oct 2015 20:02:02 Z', '2015-10-02T20:02:02Z', 2267);
