INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2270, 'Project Manager at TubeMogul (Emeryville, CA)', '&lt;p&gt;We are seeking a Project Manager who is passionate about getting things done quickly and efficiently. The Project Manager will support the existing Project Management team in ensuring roadmap execution, project reporting, team efficiency, and process evolution and adherence. The Project Management team utilizes agile methodologies to ensure all teams are delivering value to our customers rapidly and predictably.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The Project Manager will report directly in to the Director of Project Management.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Project planning, tracking, and reporting&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Facilitate meetings, including project kickoffs, sprint planning, daily stand-ups, and retrospectives&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ensure projects remain on track and mitigate risks&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Coach Product Managers, Developers, and Development Leads on effective processes and best practices&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Assist in Release Management&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Gather and report on team and roadmap metrics&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Manage and create process documentation and training&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Onboard new members of Engineering and Product Management teams&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Gather feedback from teams and contribute to process evolution&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Configure and customize JIRA and Confluence for team needs&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Emeryville, CA', 'http://careers.stackoverflow.com/jobs/99265/project-manager-tubemogul', 'Fri, 02 Oct 2015 20:02:02 Z', '2015-10-02T20:02:02Z', 2270);
