INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2278, 'Senior Software Engineer at Ivenix (Amesbury, MA)', '&lt;p&gt;&lt;strong&gt;The Position&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design and implement medical applications running on an embedded Linux processor using Qt&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design and develop software that interacts with end-users, hardware devices, databases and the Web&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Review and engage in the development of software specifications compliant with our established quality system and design controls&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;About You&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You want to put your fingerprint on something that will help bring medical care to the state of the art. You consider software craftsmanship an absolute necessity for a highly productive team. You use system-based thinking and thrive as a member of an agile, cross functional team. You know how to manage multiple tasks concurrently and correctly prioritize them. You are able to collaborate across hardware and software engineering aspects of system development to achieve great results. You also have the experience and knowledge we are looking for:&lt;/p&gt;', 'Amesbury, MA', 'http://careers.stackoverflow.com/jobs/94748/senior-software-engineer-ivenix', 'Fri, 02 Oct 2015 19:02:02 Z', '2015-10-02T19:02:02Z', 2276);
