INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2283, 'Front End Developer at Muster (Richmond, VA)', '&lt;p&gt;Muster is a cloud based web platform that enables professional associations and nonprofits to engage their membership in the legislative process.&lt;/p&gt;&lt;p&gt;We are seeking an experienced front end developer who can take a design concept and bring it to life through elegant coding. You will be one of the first developers to join our team and will have the unique opportunity to help shape the future of our product.&lt;/p&gt;&lt;p&gt;The majority of the work you will be doing will focus on new design // development with the remainder concentrating on improving Muster''s UX.&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Take a look at us here -- &lt;a href="http://www.muster.com/" rel="nofollow" rel="nofollow" rel="nofollow"&gt;www.muster.com&lt;/a&gt; -- and if this looks like a fit then we need 2 important things from you: &lt;/p&gt;&lt;p&gt;(1) A short message describing why this seems like a good fit to you&lt;/p&gt;&lt;p&gt;(2) A link to your GitHub profile&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;This position is a full-time, on site role in Richmond, VA. Salary will be based on experience and will range from 45k-85k.&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;br&gt;&lt;p&gt;&lt;strong&gt;Benefits&lt;/strong&gt;&lt;/p&gt;&lt;br&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Competitive salary based on experience&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with an exciting and energetic team in an attractive downtown RVA office space [with free parking!]&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Stock options for employees&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Paid holidays and vacation&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Richmond, VA', 'http://careers.stackoverflow.com/jobs/96902/front-end-developer-muster', 'Fri, 02 Oct 2015 19:02:02 Z', '2015-10-02T19:02:02Z', 2283);
