INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2286, 'Senior Back-end Engineer - Python at EF Learning Labs London (London, UK)', '&lt;p&gt;We are a small team developing software for language learners. Our focus is on high-quality mobile tools, and the services to track, measure and adaptively generate materials to power them, building up from the fundamentals of vocabulary, pronunciation and grammar.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our products can stand-alone or link into EF&amp;rsquo;s broader language programs. They will help a large global audience master a second language.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are a self-determined team looking at smart ways of using technology in language learning &amp;ndash; work is challenging and varied. There is little code legacy. We work with first-class hardware and tools.&lt;/p&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/99205/senior-back-end-engineer-python-ef-learning-labs-london', 'Fri, 02 Oct 2015 19:02:02 Z', '2015-10-02T19:02:02Z', 2286);
